from collections import defaultdict


def base_counter(sequence):
    nucleotide_frequencies = defaultdict(int)

    for base in list(sequence):
        nucleotide_frequencies[base] +=1

    print_base_frequencies(nucleotide_frequencies)
    return nucleotide_frequencies


def print_base_frequencies(base_frequencies):
    for base, freq in base_frequencies.items():
        print(f'{base}: {freq}')


example_sequence = 'AGCTTTTCATTCTGACTGCAACGGGCAATATGTCTCTGTGTGGATTAAAAAAAGAGTGTCTGATAGCAGC'
test_sequence = ''

base_counter(example_sequence)