rna_codon_dct = {'UUU': 'F', 'CUU': 'L', 'AUU': 'I', 'GUU': 'V',
                 'UUC': 'F', 'CUC': 'L', 'AUC': 'I', 'GUC': 'V',
                 'UUA': 'L', 'CUA': 'L', 'AUA': 'I', 'GUA': 'V',
                 'UUG': 'L', 'CUG': 'L', 'AUG': 'M', 'GUG': 'V',
                 'UCU': 'S', 'CCU': 'P', 'ACU': 'T', 'GCU': 'A',
                 'UCC': 'S', 'CCC': 'P', 'ACC': 'T', 'GCC': 'A',
                 'UCA': 'S', 'CCA': 'P', 'ACA': 'T', 'GCA': 'A',
                 'UCG': 'S', 'CCG': 'P', 'ACG': 'T', 'GCG': 'A',
                 'UAU': 'Y', 'CAU': 'H', 'AAU': 'N', 'GAU': 'D',
                 'UAC': 'Y', 'CAC': 'H', 'AAC': 'N', 'GAC': 'D',
                 'UAA': ' ', 'CAA': 'Q', 'AAA': 'K', 'GAA': 'E',
                 'UAG': ' ', 'CAG': 'Q', 'AAG': 'K', 'GAG': 'E',
                 'UGU': 'C',    'CGU': 'R', 'AGU': 'S', 'GGU': 'G',
                 'UGC': 'C',    'CGC': 'R', 'AGC': 'S', 'GGC': 'G',
                 'UGA': ' ', 'CGA': 'R', 'AGA': 'R', 'GGA': 'G',
                 'UGG': 'W',    'CGG': 'R', 'AGG': 'R', 'GGG': 'G'}


def translation(codon):
    return rna_codon_dct[codon]


def rna_to_codons(sequence):
    codon_idxs = range(0, len(sequence), 3)
    for i in codon_idxs:
        yield sequence[i:(i+3)]


def translate_rna_to_aminoacids(rna):
    codons = rna_to_codons(rna)
    return ''.join([translation(codon) for codon in codons])


print(translate_rna_to_aminoacids('AUGGCCAUGGCGCCCAGAACUGAGAUCAAUAGUACCCGUAUUAACGGGUGA'))
rna = ''
print(translate_rna_to_aminoacids(rna))